window.addEventListener('DOMContentLoaded',()=>{lazy()});async function lazy(){let el=document.getElementsByName("lazy");let e;for(let i=0;i<el.length;i+=1){e=el[i];if(isInViewport(e)){let response=await fetch(e.getAttribute("lazy-src"));let blob=await response.blob();let url=URL.createObjectURL(blob);e.src=url}}}window.addEventListener('scroll',function(){lazy()},false);function isInViewport(element){let bounding=element.getBoundingClientRect();return(bounding.top>=0||bounding.left>=0||bounding.bottom<=(window.innerHeight||document.documentElement.clientHeight)||bounding.right<=(window.innerWidth||document.documentElement.clientWidth))}